"rayyan_renamed_trimmed_cleaned_null" AS (
    -- NULL Imputation: Impute Null to Disguised Missing Values
    -- authors: ['{NULL}', '{}']
    SELECT 
        CASE
            WHEN "authors" = '{NULL}' THEN NULL
            WHEN "authors" = '{}' THEN NULL
            ELSE "authors"
        END AS "authors",
        "article_language",
        "journal_issn",
        "journal_issue",
        "journal_title",
        "publication_date",
        "article_id",
        "journal_abbreviation",
        "article_pages",
        "article_title",
        "journal_volume"
    FROM "rayyan_renamed_trimmed_cleaned"
),

"rayyan_renamed_trimmed_cleaned_null_casted" AS (
    -- Column Type Casting: 
    -- authors: from VARCHAR to JSON
    -- journal_issue: from DECIMAL to INT
    -- publication_date: from VARCHAR to DATE
    -- journal_volume: from DECIMAL to INT
    SELECT
        "article_language",
        "journal_issn",
        "journal_title",
        "article_id",
        "journal_abbreviation",
        "article_pages",
        "article_title",
        CASE
            WHEN regexp_full_match("authors", '\{"?[A-Z]\.?\s?[A-Z]?\.?\s?[A-Za-z-]+\"?\}') THEN CAST('[' || REGEXP_REPLACE("authors", '\{(.*)\}', '\1') || ']' AS JSON)
            WHEN regexp_full_match("authors", '[A-Za-z0-9/]+(\s[A-Za-z0-9&]+)+') THEN CAST('["' || REPLACE("authors", '"', '\"') || '"]' AS JSON)
            WHEN regexp_full_match("authors", '\{"([A-Za-z\s\.,-]+)(,\s[A-Za-z\s\.,-]+)*"\}') THEN CAST(REGEXP_REPLACE(REGEXP_REPLACE("authors", '\{', '['), '\}', ']') AS JSON)
        END AS "authors",
        CAST("journal_issue" AS INT) AS "journal_issue",
        strptime("publication_date", '%m/%d/%y') AS "publication_date",
        CAST("journal_volume" AS INT) AS "journal_volume"
    FROM "rayyan_renamed_trimmed_cleaned_null"
)

